	"Id"	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
	"Pair"	INTEGER NOT NULL,
	"Timestamp"	datetime NOT NULL,
	"TradeDirection"	INTEGER NOT NULL,
	"Price"	REAL NOT NULL,
	"Amount"	REAL NOT NULL,
	FOREIGN KEY("Pair") REFERENCES "Pair"("Id")
);
