	"Id"	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
	"Ticker"	TEXT NOT NULL,
	"BaseCurrency"	INTEGER NOT NULL,
	"QuoteCurrency"	INTEGER NOT NULL,
	FOREIGN KEY("BaseCurrency") REFERENCES "Currency"("Id")
);
